<?php//下一篇function theNext($widget, $default = NULL){    $db = Typecho_Db::get();    $sql = $db->select()->from('table.contents')              ->where('table.contents.created > ?', $widget->created)              ->where('table.contents.status = ?', 'publish')              ->where('table.contents.type = ?', $widget->type)              ->where('table.contents.password IS NULL')              ->order('table.contents.created', Typecho_Db::SORT_ASC)              ->limit(1);    $content = $db->fetchRow($sql);     if ($content) {        $content = $widget->filter($content);        $link = '<a href="' . $content['permalink'] . '" title="下一篇:' . $content['title'] . '">→</a>';        echo $link;    } else {        echo $default;    }} //上一篇function thePrev($widget, $default = NULL){    $db = Typecho_Db::get();    $sql = $db->select()->from('table.contents')              ->where('table.contents.created < ?', $widget->created)              ->where('table.contents.status = ?', 'publish')              ->where('table.contents.type = ?', $widget->type)              ->where('table.contents.password IS NULL')              ->order('table.contents.created', Typecho_Db::SORT_DESC)              ->limit(1);    $content = $db->fetchRow($sql);     if ($content) {        $content = $widget->filter($content);        $link = '<a href="' . $content['permalink'] . '" title="上一篇:' . $content['title'] . '">←</a>';        echo $link;    } else {        echo $default;    }}